CREATE TABLE statut (code char(8) NOT NULL, libelle int(10) NOT NULL UNIQUE, description varchar(255)) CHARACTER SET UTF8;
CREATE TABLE droit (code char(14) NOT NULL, libelle varchar(50) NOT NULL UNIQUE, codeOperation char(14) NOT NULL, codeStatut char(8) NOT NULL, codeGroupe char(14) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE utilisateur (code char(14) NOT NULL, nom varchar(100) NOT NULL, dateNaissance date, motDePasse varchar(50) NOT NULL, codeLocalisation char(14) NOT NULL, codeStatut char(8) NOT NULL, codeGroupe char(14) NOT NULL, codeLieuNaissance char(14) NOT NULL, codeTypeUtilisateur char(14) NOT NULL, codeSexe char(6) NOT NULL, codeCleChiffrement char(14) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE localisation (code char(14) NOT NULL, libelle varchar(100) NOT NULL, lattitude real NOT NULL, longitude real NOT NULL, codeLocalisationPere char(14) NOT NULL, codeStatut char(8) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE tablecode (code char(4) NOT NULL, libelleTable varchar(100) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE historiquecreationelement (code char(14) NOT NULL, valeurChamp varchar(255) NOT NULL, dateCreation timestamp NOT NULL, dateActivation timestamp NOT NULL, signature varchar(100) NOT NULL, codeUtilisateur char(14) NOT NULL, codeTableCode char(4) NOT NULL, codeLieuCreation char(14) NOT NULL, codeLieuActivation char(14) NOT NULL, codeStatut char(8) NOT NULL, codeElement char(14) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE historiquesuppressionelement (code char(14) NOT NULL, valeurChamp varchar(255), dateSuppression timestamp NOT NULL, dateActivation timestamp NOT NULL, signature varchar(100), codeUtilisateur char(14) NOT NULL, codeTableCode char(4) NOT NULL, codeLieuSuppression char(14) NOT NULL, codeLieuActivation char(14) NOT NULL, codeStatut char(8) NOT NULL, codeElement char(14) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE historiqueupdateelement (code char(14) NOT NULL, valeurAncienChamp varchar(255), valeurNouveauChamp varchar(255) NOT NULL, dateUpdate timestamp NOT NULL, dateActivation timestamp NOT NULL, signature varchar(100), codeElement char(14) NOT NULL, codeStatut char(8) NOT NULL, codeUtilisateur char(14) NOT NULL, codeTableCode char(4) NOT NULL, codeLieuUpdate char(14) NOT NULL, codeLieuActivation char(14) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE groupe (code char(14) NOT NULL, libelle varchar(100) NOT NULL UNIQUE, nombreUtilisateur int(10) DEFAULT 0 NOT NULL, codeStatut char(8) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE clechiffrement (code char(14) NOT NULL, valeurCle varchar(255) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE operation (code char(14) NOT NULL, libelle varchar(50) NOT NULL UNIQUE, informationChamp varchar(255) NOT NULL, codeTypeOperation char(14) NOT NULL, codeTableCode char(4) NOT NULL, codeStatut char(8) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE typeoperation (code char(14) NOT NULL, libelle varchar(50) NOT NULL UNIQUE, codeStatut char(8) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE typeutilisateur (code char(14) NOT NULL, libelle varchar(50) NOT NULL UNIQUE, codeStatut char(8) NOT NULL) CHARACTER SET UTF8;
CREATE TABLE sexe (code char(6) NOT NULL, libelle varchar(20) NOT NULL UNIQUE, codeStatut char(8) NOT NULL) CHARACTER SET UTF8;
